INSERT INTO [dbo].[Program] ([ProgramId], [Name], [Description], [StartDate], [EndDate], [History_CreatedBy], [History_CreatedOn], [History_RevisedBy], [History_RevisedOn], [ParentProgram_ProgramId], [Owner_OrganizationId]) VALUES (2, N'Professional Fellows "On Demand" Program', N'This exchange is designed to be a flexible program that allows for a quick response to address foreign policy and public diplomacy priorities, building upon the existing Professional Fellows model.  Once approved, an On-Demand exchange program can be publicly announced promptly after a significant event or series of events, and the exchange could take place within three to six months.  Programmatic details and timelines are developed as appropriate for specific projects and specific foreign policy goals, geared towards tangible outcomes.  In order to maintain flexibility and responsiveness, programs can be carried out anywhere in the world.', N'1/1/2015 12:00:00 AM -05:00', N'1/1/2016 12:00:00 AM -05:00', 0, N'1/14/2015 12:00:00 AM -05:00', 0, N'1/14/2015 12:00:00 AM -05:00', NULL, 1)
INSERT INTO [dbo].[Program] ([ProgramId], [Name], [Description], [StartDate], [EndDate], [History_CreatedBy], [History_CreatedOn], [History_RevisedBy], [History_RevisedOn], [ParentProgram_ProgramId], [Owner_OrganizationId]) VALUES (3, N'Youth Leadership Programs (YLP)', N'The collection of programs under the Youth Leadership Program umbrella offers one-way and reciprocal exchanges for groups of high school students and educators.  Through three to four weeks of workshops, site visits, school visits, home-stays, and cultural activities with their peers, participants gain knowledge and skills related to leadership, civic responsibility and activism, community service, and global issues. ', N'1/1/2015 12:00:00 AM -05:00', N'1/1/2016 12:00:00 AM -05:00', 0, N'1/14/2015 12:00:00 AM -05:00', 0, N'1/14/2015 12:00:00 AM -05:00', NULL, 1)
INSERT INTO [dbo].[Program] ([ProgramId], [Name], [Description], [StartDate], [EndDate], [History_CreatedBy], [History_CreatedOn], [History_RevisedBy], [History_RevisedOn], [ParentProgram_ProgramId], [Owner_OrganizationId]) VALUES (4, N'Institute for Representative Government', N'Legislators from around the world travel to the U.S. on ten-day study tours to examine U.S. legislative practices at the federal and state levels. ', N'1/1/2015 12:00:00 AM -05:00', N'1/1/2016 12:00:00 AM -05:00', 0, N'1/14/2015 12:00:00 AM -05:00', 0, N'1/14/2015 12:00:00 AM -05:00', NULL, 1)
INSERT INTO [dbo].[Program] ([ProgramId], [Name], [Description], [StartDate], [EndDate], [History_CreatedBy], [History_CreatedOn], [History_RevisedBy], [History_RevisedOn], [ParentProgram_ProgramId], [Owner_OrganizationId]) VALUES (5, N'J-1 Visa Exchange Visitor Program: Secondary School Student', N'Allows foreign secondary school students enter the United States to complete up to one year of academic study at an accredited public or private secondary school and live with an American host family. ', N'1/1/2015 12:00:00 AM -05:00', N'1/1/2016 12:00:00 AM -05:00', 0, N'1/14/2015 12:00:00 AM -05:00', 0, N'1/14/2015 12:00:00 AM -05:00', NULL, 1)
INSERT INTO [dbo].[Program] ([ProgramId], [Name], [Description], [StartDate], [EndDate], [History_CreatedBy], [History_CreatedOn], [History_RevisedBy], [History_RevisedOn], [ParentProgram_ProgramId], [Owner_OrganizationId]) VALUES (6, N'Partners of the Americas', N'Exchanges with Western Hemisphere countries designed to enhance mutual understanding through personal involvement and linkages of key volunteer specialists in fields such as citizen participation, judicial reform, public administration, promotion of minority and indigenous rights, journalism, environmental and historic conservation, education, economic development and trade, and visual and performing arts.  ', N'1/1/2015 12:00:00 AM -05:00', N'1/1/2016 12:00:00 AM -05:00', 0, N'1/14/2015 12:00:00 AM -05:00', 0, N'1/14/2015 12:00:00 AM -05:00', NULL, 1)
